    private void btDeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btDeleteActionPerformed
String emp_ID=txtEmployeeID.getText();
		  int i=Integer.parseInt(emp_ID);
          if(evt.getSource()==btDelete){         
            delete(i);            
          }  
      }

	  void delete(int emp_ID){
        try{
           Class.forName("sun.jdbc.odbc.JdbcOdbcDriver");
           Connection connect = DriverManager.getConnection("jdbc:odbc:EMS");
           Statement state = connect.createStatement();
           String sql = "delete from Employee_Detail where emp_ID="+ emp_ID+"";
           state.execute(sql);
		   JOptionPane.showMessageDialog(null, "Record deleted Successfully");
		   System.out.println("Statement executed successfully");
           state.close();
           connect.close();
		}
        catch (Exception e) {
           System.out.print(e);
        }               // TODO add your handling code here:
    }//GEN-LAST:event_btDeleteActionPerformed

